html.script:
var video = document.querySelector('video');
var yeniVideoURL = 'https://ia902606.us.archive.org/23/items/kendi-dusen-aglamaz-1.-bolum/Kendi%20D%C3%BC%C5%9Fen%20A%C4%9Flamaz%201.%20B%C3%B6l%C3%BCm.ia.mp4';
video.querySelector('source').src = yeniVideoURL;

document.addEventListener('DOMContentLoaded', function() {
var video = document.querySelector('video');
video.play();
});

document.addEventListener('click', function() {
	var video = document.querySelector('video');
	video.play();
});
